    private void btnpromActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnpromActionPerformed
try {
    // Obtener los valores de los JTextFields y convertir a double
    double nota1 = Double.parseDouble(mat1.getText());
    double nota2 = Double.parseDouble(mat2.getText());
    double nota3 = Double.parseDouble(mat3.getText());
    double nota4 = Double.parseDouble(mat4.getText());

    double nota5 = Double.parseDouble(cien1.getText());
    double nota6 = Double.parseDouble(cien2.getText());
    double nota7 = Double.parseDouble(cien3.getText());
    double nota8 = Double.parseDouble(cien4.getText());
    
    double nota9 = Double.parseDouble(ing1.getText());
    double nota10 = Double.parseDouble(ing2.getText());
    double nota11 = Double.parseDouble(ing3.getText());
    double nota12 = Double.parseDouble(ing4.getText());
    
    double nota13 = Double.parseDouble(art1.getText());
    double nota14 = Double.parseDouble(art2.getText());
    double nota15 = Double.parseDouble(art3.getText());
    double nota16 = Double.parseDouble(art4.getText());
    
    Materias materias = new Materias();

    double promedioMatematicas = materias.calcularPromedioMatematicas(nota1, nota2, nota3, nota4);
    double promedioCiencias = materias.calcularPromedioCiencias(nota5, nota6, nota7, nota8);
    double promedioIngles = materias.calcularPromedioIngles(nota9, nota10, nota11, nota12);
    double promedioArte = materias.calcularPromedioArte(nota13, nota14, nota15, nota16);
    
    JOptionPane.showMessageDialog(this, 
        "El promedio de Matematicas es: " + promedioMatematicas + 
        "\nEl promedio de Ciencias es: " + promedioCiencias +
        "\nEl promedio de Ingles es: " + promedioIngles +
        "\nEl promedio de Artes es: " + promedioArte);
} catch (NumberFormatException e) {
    JOptionPane.showMessageDialog(this, "Por favor, ingresa las notas en valores numericos");
}
    }//GEN-LAST:event_btnpromActionPerformed

